import React from "react";

import { Swiper, SwiperSlide } from "swiper/react";

import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";

import { Autoplay, Pagination } from "swiper";

const featureTest = [1, 2, 3, 4, 5];

import "swiper/css";
const Hero = () => {
  return (
    <div>
      <section className="hero-area">
        <div className="container">
          <div className="row">
            <div className="col-lg-8 col-12 custom-padding-right">
              <div className="slider-head">
                <div className="hero-slider">
                  <Swiper
                    spaceBetween={30}
                    centeredSlides={true}
                    autoplay={{
                      delay: 5500,
                      disableOnInteraction: false,
                    }}
                    pagination={{
                      clickable: true,
                    }}
                    modules={[Autoplay, Pagination]}
                    className="mySwiper"
                  >
                    {featureTest.map((count, index) => (
                      <SwiperSlide>
                        <div
                          className="single-slider"
                          style={{
                            backgroundImage: `url(assets/images/hero/slider-bg1.jpg)`,
                          }}
                        >
                          <div className="content">
                            <h2>
                              <span>No restocking fee ($35 savings)</span>
                              M75 Sport Watch
                            </h2>
                            <p>
                              Lorem ipsum dolor sit amet, consectetur adipiscing
                              elit, sed do eiusmod tempor incididunt ut labore
                              et dolore magna aliqua.
                            </p>
                            <h3>
                              <span>Now Only</span> $320.99
                            </h3>
                            <div className="button">
                              <a href="product-grids.html" className="btn">
                                Shop Now
                              </a>
                            </div>
                          </div>
                        </div>
                      </SwiperSlide>
                    ))}
                  </Swiper>
                </div>
              </div>
            </div>

            <div className="col-lg-4 col-12">
              <div className="row">
                <div className="col-lg-12 col-md-6 col-12 md-custom-padding">
                  <div
                    className="hero-small-banner"
                    style={{
                      backgroundImage: `url('assets/images/hero/slider-bnr.jpg')`,
                    }}
                  >
                    <div className="content">
                      <h2>
                        <span>New line required</span>
                        iPhone 12 Pro Max
                      </h2>
                      <h3>$259.99</h3>
                    </div>
                  </div>
                </div>
                <div className="col-lg-12 col-md-6 col-12">
                  <div className="hero-small-banner style2">
                    <div className="content">
                      <h2>Weekly Sale!</h2>
                      <p>
                        Saving up to 50% off all online store items this week.
                      </p>
                      <div className="button">
                        <a className="btn" href="product-grids.html">
                          Shop Now
                        </a>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Hero;
