import React from "react";
import Heading from "../";

const ProductDetails = () => {
  return (
    <>
      <Heading />
      <div class="item-details section mt-150">
        <div class="container">
          <div class="top-area">
            <div class="row align-items-center">
              <div class="col-lg-6 col-md-12 col-12">
                <div class="product-images">
                  <main id="gallery">
                    <div class="main-img">
                      <img
                        src="assets/images/product-details/01.jpg"
                        id="current"
                        alt="#"
                      />
                    </div>
                    <div class="images">
                      <img
                        src="assets/images/product-details/01.jpg"
                        class="img"
                        alt="#"
                      />
                      <img
                        src="assets/images/product-details/02.jpg"
                        class="img"
                        alt="#"
                      />
                      <img
                        src="assets/images/product-details/03.jpg"
                        class="img"
                        alt="#"
                      />
                      <img
                        src="assets/images/product-details/04.jpg"
                        class="img"
                        alt="#"
                      />
                      <img
                        src="assets/images/product-details/05.jpg"
                        class="img"
                        alt="#"
                      />
                    </div>
                  </main>
                </div>
              </div>
              <div class="col-lg-6 col-md-12 col-12">
                <div class="product-info">
                  <h2 class="title">GoPro Karma Camera Drone</h2>
                  <p class="category">
                    <i class="lni lni-tag"></i> Drones:
                    <a href="/">Action cameras</a>
                  </p>
                  <h3 class="price">
                    $850<span>$945</span>
                  </h3>
                  <p class="info-text">
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed
                    do eiusmod tempor incididunt ut labore et dolore magna
                    aliqua.
                  </p>
                  <div class="col-lg-4 col-md-4 col-12">
                    <div class="form-group">
                      <label for="color">Battery capacity</label>
                      <select class="form-control" id="color">
                        <option>5100 mAh</option>
                        <option>6200 mAh</option>
                        <option>8000 mAh</option>
                      </select>
                    </div>
                  </div>
                  <div class="col-lg-4 col-md-4 col-12">
                    <div class="form-group quantity">
                      <label for="color">Quantity</label>
                      <select class="form-control">
                        <option>1</option>
                        <option>2</option>
                        <option>3</option>
                        <option>4</option>
                        <option>5</option>
                      </select>
                    </div>
                  </div>
                </div>
                <div class="bottom-content">
                  <div class="row align-items-end">
                    <div class="col-lg-4 col-md-4 col-12">
                      <div class="button cart-button">
                        <button class="btn" style={{ width: `100%` }}>
                          Add to Cart
                        </button>
                      </div>
                    </div>
                    <div class="col-lg-4 col-md-4 col-12">
                      <div class="wish-button">
                        <button class="btn">
                          <i class="lni lni-reload"></i> Compare
                        </button>
                      </div>
                    </div>
                    <div class="col-lg-4 col-md-4 col-12">
                      <div class="wish-button">
                        <button class="btn">
                          <i class="lni lni-heart"></i> To Wishlist
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};

export default ProductDetails;

